mapper.select:
        SELECT * 
        FROM APPLY

			cr_num IN (
        		SELECT cr_num FROM COMPANYRESUME WHERE M_NUM=#{m_num}
        	)
			AND STATE = #{state}

      	SELECT count(*) AS CNT
        FROM APPLY

			cr_num IN (
        		SELECT cr_num FROM COMPANYRESUME WHERE M_NUM=#{m_num}
        	)
			AND STATE = #{state}

mapper.update:
UPDATE APPLY SET 
   state = #{state}
WHERE a_id = #{a_id}
