      SELECT J.j_id , J.d_date, C.c_name, J.job_type, J.employment_type, J.payment_type, J.payment, J.career, J.edu, J.r_date, J.detail
      FROM (JOBINFO J INNER JOIN COMPANY C
      ON J.c_num = C.c_num) INNER JOIN ZZIM Z
      ON J.j_id = z.guzzik_id



               and J.employment_type in 

                  #{employmenttype}





               and J.payment_type in 

                  #{paymenttype}





			  	and (

		            C.address LIKE CONCAT(#{i}, '%')

			    )




               and C.c_type in 

                  #{type}




         		and Z.user_id = #{user_id}



               and J.job_type LIKE CONCAT('%', #{keyword}, '%')


               and C.c_name LIKE CONCAT('%', #{keyword}, '%')

SELECT J.j_id , J.d_date, C.c_name, J.job_type, J.employment_type, J.payment_type, J.payment, J.career, J.edu, J.r_date, J.detail FROM JOBINFO J INNER JOIN COMPANY C ON J.c_num = C.c_num
and J.employment_type in
#{employmenttype}
and J.payment_type in
#{paymenttype}
and (
C.address LIKE CONCAT(#{i}, '%')
)
and C.c_type in
#{type}
and J.job_type LIKE CONCAT('%', #{keyword}, '%')
and C.c_name LIKE CONCAT('%', #{keyword}, '%')
